import React, { Component } from "react";
import PropTypes from "prop-types";
import "./Img.css";

export class Img extends Component {
  openGallery = (e) => {
    console.log("Open gallery");
    let src = e.src;
    alert(src);
  };
  render() {
    let urls = [];
    let images = this.props.images;

    images.map((val) => {
      let url = this.props.getImageUrl(
        val.farm,
        val.server,
        val.id,
        val.secret
      );
      urls.push(url);
      images = "";
      return null;
    });
    return (
      <div className="row">
        {React.Children.toArray(
          urls.map((val, i) => (
            <div className="column" onClick={this.openGallery(this.id)}>
              <img src={val} id={i} alt="flickr-img" />
            </div>
          ))
        )}
      </div>
    );
  }
}

//PropTypes
Img.propTypes = {
  getImageUrl: PropTypes.func.isRequired,
  images: PropTypes.array,
};

export default Img;
