import React, { Component } from "react";
import { HashRouter as Router, Route } from "react-router-dom";
import Header from "./layout/Header";
import SearchFlickr from "./layout/SearchFlickr";
import Img from "./Img";
import About from "./pages/About";
import "./App.css";
import axios from "axios";

class App extends Component {
  state = {
    images: [],
  };
  searchImg = (query) => {
    // console.log(query);
    axios
      .get(
        "https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=b54580f369a7eeebecb2004dc429d08f&format=json&nojsoncallback=1&text=" +
          query
      )
      .then((response) => {
        //console.log(response);
        this.setState({ images: response.data.photos.photo });
        console.log(this.state.images);
      })
      .catch((err) => {
        console.log(err);
      });

    axios.get("https://api.chucknorris.io/jokes/random").then((getChuck) => {
      console.log(getChuck.data.value);
    });
  };

  getImageUrl(farm, server, id, secret) {
    return `"https://farm${farm}.staticflickr.com/${server}/${id}_${secret}_m.jpg"`;
  }

  render() {
    if (this.images) {
      return (
        <Router>
          <div className="App">
            <div className="container">
              <Header />
              <Route
                exact
                path="/"
                render={(props) => (
                  <React.Fragment>
                    <SearchFlickr searchImg={this.searchImg} />
                    <Img images={this.images} getImageUrl={this.getImageUrl} />
                  </React.Fragment>
                )}
              />
              <Route path="/about" component={About} />
            </div>
          </div>
        </Router>
      );
    }
  }
}
export default App;
