import React, { Component } from "react";
import PropTypes from "prop-types";

class Gallery extends Component {
  constructor(props) {
    super(props);
    this.id = 0; // I declare the variable here
  }

  onClickPrevious = () => {
    console.log(this.id);
    if (this.id - 1 === -1) {
      this.id = this.props.imgGal.length;
    } else {
      this.id = this.id - 1;
    }
  };

  render() {
    let imgGal = this.props.imgGal;
    function onClickNext() {
      if (this.id + 1 === this.props.imgGal.length) {
        this.id = 0;
      } else {
        this.id = this.id + 1;
      }
    }

    if (!imgGal[this.id]) {
      console.log("Emty");
      //alert("You have to make a selection first");
      return null;
    }
    return (
      <React.Fragment>
        <img
          src={this.props.imgGal[this.id]}
          style={{ maxHeight: "40%", maxWidth: "40%" }}
          alt="imag"
        />{" "}
        <br />
        <button style={{ fontSize: "18px" }} onClick={this.onClickPrevious}>
          {" "}
          Previous{" "}
        </button>
        <button
          style={{ marginLeft: "5px", fontSize: "18px" }}
          onClick={this.onClickNext}
        >
          {" "}
          Next{" "}
        </button>
      </React.Fragment>
    );
  }
}
Gallery.propTypes = {
  imgGal: PropTypes.array.isRequired,
};
export default Gallery;
