import React, { Component } from "react";
import { HashRouter as Router, Route } from "react-router-dom";
import Header from "./layout/Header";
import SearchFlickr from "./layout/SearchFlickr";
import Img from "./Img";
import About from "./pages/About";
import "./App.css";
import axios from "axios";

class App extends Component {
  state = {
    images: "bilder",
  };
  searchImg = (query) => {
    console.log(query);
    axios
      .get(
        "https://api.flickr.com/services/feeds/photos_public.gne?tags=kitten&format=json&nojsoncallback=true"
      )
      .then((response) => {
        console.log(response.data.items);
        this.setState({
          items: response.data.items,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  render() {
    return (
      <Router>
        <div className="App">
          <div className="container">
            <Header />
            <Route
              exact
              path="/"
              render={(props) => (
                <React.Fragment>
                  <SearchFlickr searchImg={this.searchImg} />
                  <Img searchImg={this.searchImg} />
                </React.Fragment>
              )}
            />
            <Route path="/about" component={About} />
          </div>
        </div>
      </Router>
    );
  }
}
export default App;
