import React, { Component } from "react";
import PropTypes from "prop-types";

class Gallery extends Component {
  onClickNext = (i) => {
    if (this.props.imgGal[i] + 1 === this.props.imgGal.length) {
      this.setState({
        index: 0,
      });
    } else {
      this.setState({
        index: this.props.imgGal.index + 1,
      });
    }
  };
  onClickPrevious = (i) => {
    if (this.props.imgGal[i] - 1 === -1) {
      this.setState({
        index: this.props.imgGal.length - 1,
      });
    } else {
      this.setState({
        index: this.props.imgGal.index - 1,
      });
    }
  };

  render() {
    if (!this.props.imgGal) {
      console.log("Emty");
      //alert("You have to make a selection first");
      return null;
    }
    console.log(this.props.imgGal);
    return (
      <React.Fragment>
        <img
          src={this.props.imgGal}
          style={{ maxHeight: "40%", maxWidth: "40%" }}
          alt="imag"
        />{" "}
        <br />
        <button
          style={{ fontSize: "18px" }}
          onClick={this.onClickPrevious(this.props.imgGal.index)}
        >
          {" "}
          Previous{" "}
        </button>
        <button
          style={{ "margin-left": "5px", fontSize: "18px" }}
          onClick={this.onClickNext(this.props.imgGal.index)}
        >
          {" "}
          Next{" "}
        </button>
      </React.Fragment>
    );
  }
}
Gallery.propTypes = {
  imgGal: PropTypes.array.isRequired,
};
export default Gallery;
