import React, { Component } from "react";
import PropTypes from "prop-types";
import axios from "axios";

export class SearchFlickr extends Component {
  state = {
    query: "",
  };
  onSubmit = (e) => {
    //e.preventDefault();
    console.log(this.state.query);
    this.props.addImg(this.state.query);
    this.setState({ query: "" });
    axios
      .get(
        "https://api.flickr.com/services/feeds/photos_public.gne?tags=kitten&format=json&nojsoncallback=true"
      )
      .then((response) => {
        console.log(response.data.items);
        this.setState({
          items: response.data.items,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  onChange = (e) => this.setState({ [e.target.name]: e.target.value });
  render() {
    return (
      <form onSubmit={this.onSubmit} style={{ display: "flex" }}>
        <input
          type="text"
          name="query"
          style={{ flex: "10", padding: "5px" }}
          placeholder="Search Flickr"
          value={this.state.query}
          onChange={this.onChange}
        />

        <input
          type="submit"
          value="Search"
          className="btn"
          style={{ flex: "1" }}
        />
      </form>
    );
  }
}
//PropTypes
SearchFlickr.propTypes = {
  //this.state.func.addImg is requierd
  query: PropTypes.object.isRequired,
};

export default SearchFlickr;
