import React, { Component } from "react";
import PropTypes from "prop-types";

class Gallery extends Component {
  render() {
    let imgGal = this.props.imgGal;
    let id = 0;
    this.setState({ imgGal: [...this.props.imgGal, ""] });
    console.log(this.props.imgGal);
    function onClickNext() {
      if (id + 1 === imgGal.length) {
        id = 0;
      } else {
        id = id + 1;
      }
    }
    function onClickPrevious() {
      if (id - 1 === -1) {
        id = imgGal.length;
      } else {
        id = id - 1;
      }
    }
    if (!imgGal[id]) {
      console.log("Emty");
      //alert("You have to make a selection first");
      return null;
    }
    return (
      <React.Fragment>
        <img
          src={imgGal[id]}
          style={{ maxHeight: "40%", maxWidth: "40%" }}
          alt="imag"
        />{" "}
        <br />
        <button style={{ fontSize: "18px" }} onClick={onClickPrevious()}>
          {" "}
          Previous{" "}
        </button>
        <button
          style={{ marginLeft: "5px", fontSize: "18px" }}
          onClick={onClickNext()}
        >
          {" "}
          Next{" "}
        </button>
      </React.Fragment>
    );
  }
}
Gallery.propTypes = {
  imgGal: PropTypes.array.isRequired,
};
export default Gallery;
