import React, { Component } from "react";
import PropTypes from "prop-types";

class Gallery extends Component {
  onClickNext = () => {
    if (this.props.index + 1 === this.state.src.length) {
      this.setState({
        index: 0,
      });
    } else {
      this.setState({
        index: this.state.index + 1,
      });
    }
  };
  onClickPrevious = () => {
    if (this.state.index - 1 === -1) {
      this.setState({
        index: this.state.src.length - 1,
      });
    } else {
      this.setState({
        index: this.state.index - 1,
      });
    }
  };

  render() {
    return (
      <React.Fragment>
        <img
          src={this.state.src[this.state.index]}
          style={{ maxHeight: "40%", maxWidth: "40%" }}
          alt="imag"
        />{" "}
        <br />
        <button style={{ fontSize: "18px" }} onClick={this.onClickPrevious}>
          {" "}
          Previous{" "}
        </button>
        <button
          style={{ "margin-left": "5px", fontSize: "18px" }}
          onClick={this.onClickNext}
        >
          {" "}
          Next{" "}
        </button>
      </React.Fragment>
    );
  }
}
Gallery.propTypes = {
  imgGal: PropTypes.array.isRequired,
};
export default Gallery;
